import React from "react";
import './Catalog.css';
class Catalog extends React.Component { 
    constructor(props) {
        super(props);
   
        this.state = {
            items: [],
            DataisLoaded: false
        };
    }
    componentDidMount() {
        fetch("https://restcountries.com/v3.1/all")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    items: json,
                    DataisLoaded: true
                });
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h1> Pleses wait some time.... </h1> </div> ;
   
        return (
        <div className = "">
            <div>
                <span>Flag</span>
                <span>Country Name</span>
                <span>2 Character Country Code</span>
                <span>3 Character country Code</span>
                <span>Native Country Name</span>
                <span>Country Calling Code</span>
            </div>
            {
                items.map((item) => ( 
                    <div >
                        <span><img src={item.flags} alt="flag" /></span>
                        <span>{item.name.official} </span> 
                        <span>{item.cca2}</span>
                        <span>{item.cca3}</span>
                        
                        <span>{item.altSpellings}</span>
                    </div>
                ))
            }
        </div>
    );
}
}

export default Catalog;
