import fetch from 'node-fetch';
var countryData={
    items:[],
    DataisLoaded:false
}
    fetch("https://restcountries.com/v3.1/all")
        .then((res) => res.json())
            .then((json) => {
                countryData= {
                    items: json,
                    DataisLoaded: true
                };
            })
//console.log(countryData.items);
export default countryData;
