import { useState } from "react";

const Table = ({items})=>{
    const [data,setData]=useState(items);
    const [order,setOrder]=useState("ASC");
    const sorting = ()=>{
        if(order==="ASC"){
            const sorted = [...data].sort((a,b)=>a.name.official.toLowerCase() > b.name.official.toLowerCase() ? 1 : -1);
            setData(sorted);
            setOrder("DESC");
        }
        if(order==="DESC"){
            const sorted = [...data].sort((a,b)=>a.name.official.toLowerCase() < b.name.official.toLowerCase() ? 1 : -1);
            setData(sorted);
            setOrder("ASC");
        }
    

    };
    sorting();
    
    return(
        <table>
            <thead>
                <tr>
                    <th>Flag Country</th>
                    <th onClick={()=>sorting()}>Country Name</th>
                    <th>2 Character Country Code</th>
                    <th>3 Character country Code</th>
                    <th>Native Country Name</th>
                    <th>Country Calling Code</th>
                </tr>
            </thead>
            <tbody>
                {data.map((item)=>(
                    <tr>
                        <td><img src={item.flags.png} alt="flag" /></td>
                        <td>{item.name.official} </td> 
                        <td>{item.cca2}</td>
                        <td>{item.cca3}</td>
                        <td>{item.translations.zho?.official??item.name.nativeName.zho.official}</td>
                        <td>{item.altSpellings}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
}

export default Table;
