const Table = ({data})=>{
    let value=false;
    const asc =(a,b)=>a.name.official > b.name.official ? 1 : -1; 
    const desc =(a,b)=>a.name.official < b.name.official ? 1 : -1; 
    return(
        <table>
            <thead>
                <tr>
                    <th>Flag Country</th>
                    <th onClick={value=!value}>Country Name</th>
                    <th>2 Character Country Code</th>
                    <th>3 Character country Code</th>
                    <th>Native Country Name</th>
                    <th>Country Calling Code</th>
                </tr>
            </thead>
            <tbody>
                {data.sort((value)=>{
                    if(value)return desc;
                    return asc;
                }).map((item)=>(
                    <tr>
                        <td><img src={item.flags.png} alt="flag" /></td>
                        <td>{item.name.official} </td> 
                        <td>{item.cca2}</td>
                        <td>{item.cca3}</td>
                        <td>{item.translations.zho?.official??item.name.nativeName.zho.official}</td>
                        <td>{item.altSpellings}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
}

export default Table;
