import './App.css';
import React from "react";
import TextField from '@material-ui/core/TextField';
import Autocomplete, { createFilterOptions } from '@material-ui/lab/Autocomplete';

class App extends React.Component { 
    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
        this.filterOptions = createFilterOptions({
          matchFrom: 'start',
          stringify: option => option,
        });
    }
    componentDidMount() {
        fetch("https://restcountries.com/v3.1/all")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    items: json,
                    DataisLoaded: true
                });
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        const filterOptions = this.filterOptions;
        if (!DataisLoaded) return <div>
            <h1> Pleses wait some time.... </h1> </div> ;

        return (
        <div className = "">
          <div style={{ marginLeft: '30%', marginTop: '60px', marginBottom:'60px' }}>
            <Autocomplete
              style={{ width: 500 }}
              freeSolo
              filterOptions={filterOptions}
              options={items.map((item)=>(
                item.name.official
              ))}
              renderInput={(params) => (
                <TextField {...params}
                  variant="outlined"
                  label="Search Country"
                />
              )}
            />
          </div>
          <div className = "catalog">
            <div>
                <span>Flag Country</span>
                <span>Country Name</span>
                <span>2 Character Country Code</span>
                <span>3 Character country Code</span>
                <span>Native Country Name</span>
                <span>Country Calling Code</span>
            </div>
            {
                items.map((item) => ( 
                    <div >
                        <span><img src={item.flags.png} alt="flag" /></span>
                        <span>{item.name.official} </span> 
                        <span>{item.cca2}</span>
                        <span>{item.cca3}</span>
                        <span><p>{item.translations.zho.official}</p></span>
                        <span>{item.altSpellings}</span>
                    </div>
                ))
            }
          </div>
        </div>
    );
}
}

export default App;
